static void ErrLog(const CHAR *sFormat, ...)
{
    va_list argp;
    va_start(argp, sFormat);
    vsnprintf(gsPCIE_SW_LastErr, sizeof(gsPCIE_SW_LastErr) - 1, sFormat, argp);
#if defined(DEBUG)
    WDC_Err("PCIE_SW lib: %s", PCIE_SW_GetLastErr());
#endif
    va_end(argp);
}

static void TraceLog(const CHAR *sFormat, ...)
{
#if defined(DEBUG)
    CHAR sMsg[256];
    va_list argp;
    va_start(argp, sFormat);
    vsnprintf(sMsg, sizeof(sMsg) - 1, sFormat, argp);
    WDC_Trace("PCIE_SW lib: %s", sMsg);
    va_end(argp);
#endif
}

